CLAIM, DAMAGES OR OTHER LIABILITY, WHETHER IN AN ACTION OF CONTRACT,
TORT OR OTHERWISE, ARISING FROM, OUT OF OR IN CONNECTION WITH THE
SOFTWARE OR THE USE OR OTHER DEALINGS IN THE SOFTWARE.
*/
//solhint-disable max-line-length
//solhint-disable no-inline-assembly

contract CloneFactory {

  function createClone(address target) internal returns (address result) {
    bytes20 targetBytes = bytes20(target);
    assembly {
      let clone := mload(0x40)
      mstore(clone, 0x3d602d80600a3d3981f3363d3d373d3d3d363d73000000000000000000000000)
      mstore(add(clone, 0x14), targetBytes)
      mstore(add(clone, 0x28), 0x5af43d82803e903d91602b57fd5bf30000000000000000000000000000000000)
      result := create(0, clone, 0x37)
    }
  }

  function isClone(address target, address query) internal view returns (bool result) {
    bytes20 targetBytes = bytes20(target);
    assembly {
      let clone := mload(0x40)
      mstore(clone, 0x363d3d373d3d3d363d7300000000000000000000000000000000000000000000)
      mstore(add(clone, 0xa), targetBytes)
      mstore(add(clone, 0x1e), 0x5af43d82803e903d91602b57fd5bf30000000000000000000000000000000000)

      let other := add(clone, 0x40)
      extcodecopy(query, other, 0, 0x2d)
      result := and(
        eq(mload(clone), mload(other)),
        eq(mload(add(clone, 0xd)), mload(add(other, 0xd)))
      )
    }
  }
}

contracts/User.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";


/**
 * @title User
 * @notice Allow to receive ERC721 token and store all data related to a user
 * @dev This contract is used as a proxy contract. Each user is created via UserFactory.
 * @author Watson Bosquet
*/
contract User is IERC721Receiver {
  struct Participant {
    address userAddress;
    string firstname;
    string lastname;
    string email;
    string country;
    uint256 dob;
  }

  Participant participant;
  mapping(uint256 => uint256) tokenIds;

  event UserCreated(address indexed _userAddress, string _firstname, string _lastname, string _email, string country, uint256 _dob);
  event ERC721ContractAddressEmitterUpdated(address indexed previousERC721ContractAddressEmitter, address indexed newERC721ContractAddressEmitter);

  /**
   * @notice Create a user. Initialize all his information and emit a event
   * @param _firstname  User's firstname
   * @param _lastname User's lastname
   * @param _email User's email
   * @param _country User's country
   * @param _dob User's date of birth stored as a timestamp
   */
  function create(string memory _firstname, string memory _lastname, string memory _email, string memory _country, uint256 _dob) external {
    require(participant.userAddress == address(0), "You already have an account");
    require(bytes(_firstname).length != 0, "firstname cant be empty");
    require(bytes(_lastname).length != 0, "lastname cant be empty");
    require(bytes(_email).length != 0, "email cant be empty");
    require(bytes(_country).length != 0, "country cant be empty");

    participant = Participant(msg.sender, _firstname, _lastname, _email, _country, _dob);
  }

  /**
   * @notice This contract can receive the reward NFTs
   * @dev TODO could implement the fact that it can only receive NFT from Event contract
   * put a address private ERC721ContractAddressEmitter;
   * add to this function require(ERC721ContractAddressEmitter == from, "The sender is not verified");  
   * by extension create 2 methods to update the variable through UserFactory contract:
   * - function updateERC721ContractAddressEmitter(address _newERC721ContractAddressEmitter) external onlyOwner 
   * allow to update the value of ERC721ContractAddressEmitter
   * -  function getERC721ContractAddressEmitter() external view onlyOwner returns(address) to get the value from UserFactory, to be able to 
   * check the value when needed
   *
   * Plus here we should store the tokenId into a array of tokenIds 
   */
  function onERC721Received(address, address, uint256, bytes calldata) external pure returns (bytes4) {
    return IERC721Receiver.onERC721Received.selector;
  }
}

contracts/UserFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "./CloneFactory.sol";
import "./User.sol";

/**
 * @title UserFactory
 * @notice This factory allows Users contracts.
 * Only a regular user can create his profil and only the owner of the factory can add an admin
 * @dev The owner of an event is the smart contract UserFactory. An admin of an event can only manipulate created methods
 * @author Watson Bosquet
*/

contract UserFactory is CloneFactory, Ownable {
  address public userProxyContractAddress;
  mapping(address => bool) private registeredUsersContracts;
  mapping(address => bool) private registeredUsers;

  event UserProxyContractAddressUpdated(address indexed previousUserProxyContractAddress, address indexed newUserProxyContractAddress);
  event UserCreated(address indexed userContractAddress, address indexed userAddress, string firstname, string lastname, string email, string country, uint256 dob);

  constructor(address _userProxyContractAddress) {
    userProxyContractAddress = _userProxyContractAddress;
  }

  /**
   * @notice Create a user. Initialize all his information and emit a event
   * @param _firstname  User's firstname
   * @param _lastname User's lastname
   * @param _email User's email
   * @param _country User's country
   * @param _dob User's date of birth stored as a timestamp
   */
  function create(
    string memory _firstname,
    string memory _lastname,
    string memory _email,
    string memory _country,
    uint256 _dob
  ) external payable {
    require(registeredUsers[msg.sender] == false, "You already have an account");

    address user = createClone(userProxyContractAddress);
    User(user).create(_firstname, _lastname, _email, _country, _dob);

    registeredUsers[msg.sender] = true;
    registeredUsersContracts[user] = true;

    emit UserCreated(user, msg.sender,  _firstname, _lastname, _email, _country, _dob);
  }

  /**
   * @notice Update the user proxy contract address
   * @param _userProxyContractAddress Address of the User contract used as proxy
   * @dev _userProxyContractAddress provided as an argument, has to be a contract address
   */
  function updateUserProxyContractAddress(address _userProxyContractAddress) external onlyOwner {
    require(userProxyContractAddress != _userProxyContractAddress, "This address is already used as the proxy");
    require(Address.isContract(_userProxyContractAddress) == true, "Provided address is not valid");

    address oldUserProxyContractAddress =  userProxyContractAddress;
    userProxyContractAddress = _userProxyContractAddress;

    emit UserProxyContractAddressUpdated(oldUserProxyContractAddress, userProxyContractAddress);
  }
}
